import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import { AttachFile, Description, PictureAsPdf } from '@material-ui/icons';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import ReactFileReader from 'react-file-reader';
import { Box, FormControlLabel, CardHeader, Divider, Grid, TextField, Select,MenuItem,FormControl,InputLabel, Checkbox   } from '@material-ui/core';
import PublishIcon from '@material-ui/icons/Publish';
import {useDropzone} from 'react-dropzone';
import MyDropZone from './MyDropZone';
import { useEffect,useState } from 'react';
const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  
  title: {
    fontSize: 14,
  },
 
});

export default function UploadFileCard() {
  const classes = useStyles();
  const [fileName, setFile] = useState([])

    useEffect(() => {
    
    }, [fileName])


  const handleFiles = (files) => {
      var reader = new FileReader();
      reader.onload = function(e) {
          // Use reader.result
          console.log(reader)
          console.log(reader.result)
      }
      reader.readAsText(files[0]);
  }

  const handlePreviewIcon = (fileObject, classes) => {
  const {type} = fileObject.file
  const iconProps = {
    className : classes.image,
  }


  switch (type) {
    case "application/msword":
    case "application/vnd.openxmlformats-officedocument.wordprocessingml.document":
      return <Description {...iconProps} />
    case "application/pdf":
      return <PictureAsPdf {...iconProps} />
    default:
      return <AttachFile {...iconProps} />
  }
}

const {acceptedFiles, getRootProps, getInputProps} = useDropzone();
  
  const files = acceptedFiles.map(file => (
    <li key={file.path}>
      {file.path} - {file.size} bytes
    </li>
  ));


  return (
    <Card >
      <Box color="whitesmoke" bgcolor="info.main">
        <Grid container>
          <Grid item lg={11}>
            <Typography paddingLeft='1%' variant="h2" component="h2">
              Upload file
            </Typography>
          </Grid>
          <Grid item justifySelf="flex-end" lg={1}>
            <PublishIcon fontSize="large"/>
          </Grid>
        </Grid>
        
      </Box>
      <CardContent>
        <Grid container spacing={3}>
          {/* <Dropzone onDrop={acceptedFiles => console.log(acceptedFiles)}>
            {({getRootProps, getInputProps}) => (
              <section>
                <div {...getRootProps()}>
                  <input {...getInputProps()} />
                  <p>Drag 'n' drop some files here, or click to select files</p>
                </div>
              </section>
            )}
          </Dropzone> */}
          <Grid item lg={3}>
            <TextField required id="standard-required" label="year" placeholder="2021" />
          </Grid>
          <Grid item marginTop={1} lg={2}>
            <FormControl className={classes.formControl}>
              <InputLabel id="demo-simple-select-label">Month</InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                style={{width:"100px"}}
              >
                <MenuItem value={10}>January</MenuItem>
                <MenuItem value={20}>February</MenuItem>
                <MenuItem value={30}>March</MenuItem>
                <MenuItem value={30}>March</MenuItem>

              </Select>
            </FormControl>

          </Grid>
          <Grid item marginTop={2} lg={1}>
            <FormControlLabel
            control={<Checkbox disabled checked={false} color="primary"/>}
            label="Update"
            />
          </Grid>
           <Grid item marginTop={2} lg={2}>
            <FormControlLabel
            control={<Checkbox checked={"false"} color="primary"/>}
            label="New"
            />
          </Grid>
          <Grid item lg={12}>
            <MyDropZone setFile={setFile}/>
          
          </Grid>
          <Grid item lg={12}>
            <Typography className={classes.title} color="textSecondary" gutterBottom>
              {fileName}
            </Typography>
          </Grid>
        </Grid>
      </CardContent>
      <CardActions>
            <Button className='btn'>Upload</Button>
      </CardActions>
    </Card>
  );
}
