import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Dropzone from 'react-dropzone'

import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import ReactFileReader from 'react-file-reader';
import { Box, CardHeader, Divider, Grid } from '@material-ui/core';
import PublishIcon from '@material-ui/icons/Publish';

const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  
  title: {
    fontSize: 14,
  },
 
});

export default function UploadFileCard() {
  const classes = useStyles();
  const bull = <span className={classes.bullet}>â€¢</span>;
  const handleFiles = (files) => {
      var reader = new FileReader();
      reader.onload = function(e) {
          // Use reader.result
          console.log(reader)
          console.log(reader.result)
      }
      reader.readAsText(files[0]);
  }
  return (
    <Card >
      <Box color="whitesmoke" bgcolor="info.main">
        <Grid container>
          <Grid item lg={11}>
            <Typography paddingLeft='1%' variant="h2" component="h2">
              Upload file
            </Typography>
          </Grid>
          <Grid item justifySelf="flex-end" lg={1}>
            <PublishIcon fontSize="large"/>
          </Grid>
        </Grid>
        
      </Box>
      <CardContent>
        <Typography className={classes.title} color="textSecondary" gutterBottom>
          No files uploaded
        </Typography>
        <Dropzone>
          {dropzoneProps => {
            return (
              <div>
                <p>Drop some files here</p>
              </div>
            );
          }}
        </Dropzone>;
      </CardContent>
      <CardActions>
          <ReactFileReader handleFiles={handleFiles} fileTypes={'.csv'}>
            <Button className='btn'>Upload</Button>
          </ReactFileReader>
      </CardActions>
    </Card>
  );
}
