import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import { AttachFile, Description, PictureAsPdf } from '@material-ui/icons';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import ReactFileReader from 'react-file-reader';
import { Box, FormControlLabel, CardHeader, Divider, Grid, TextField, Select,MenuItem,FormControl,InputLabel, Checkbox   } from '@material-ui/core';
import PublishIcon from '@material-ui/icons/Publish';
import {useDropzone} from 'react-dropzone';
import MyDropZone from './MyDropZone';
import { useEffect,useState } from 'react';
import { element } from 'prop-types';
const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  
  title: {
    fontSize: 14,
  },
 
});

const months=  ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"]
export default function UploadFileCard({setIsUpdate, setIsNew, setFile, handleUploadClick}) {
  const classes = useStyles();
  const [fileName, setFileName] = useState('')
  const [isFileValid, setIsFileValid] = useState(true)
  // const [isUpdate, setIsUpdate] = useState(false)
  // const [isNew, setIsNew] = useState(false)

    useEffect(() => {
    
    }, [fileName])


 const renderFileName = () =>{
  if(isFileValid) {
    return(
      <Typography className={classes.title} color="textSecondary" gutterBottom>
                {fileName}
        </Typography>
    )
  }
  else{
    return(
      <Typography className={classes.title} color="red" gutterBottom>
                {fileName}
        </Typography>
    )
  }
 }

 const handleMonthChange = (event) => {
    console.log(event.target.value);
    
  };


  return (
    <Card >
      <Box color="whitesmoke" bgcolor="info.main">
        <Grid container>
          <Grid item lg={11}>
            <Typography paddingLeft='1%' variant="h2" component="h2">
              Upload file
            </Typography>
          </Grid>
          <Grid item justifySelf="flex-end" lg={1}>
            <PublishIcon fontSize="large"/>
          </Grid>
        </Grid>
        
      </Box>
      <CardContent>
        <Grid container spacing={3}>
          <Grid item lg={3}>
            <TextField required id="standard-required" label="year" placeholder="2021" />
          </Grid>
          <Grid item marginTop={1} lg={2}>
            <FormControl className={classes.formControl}>
              <InputLabel id="demo-simple-select-label">Month</InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                style={{width:"100px"}}
              >
                <MenuItem value={10}>January</MenuItem>
                <MenuItem value={20}>February</MenuItem>
                <MenuItem value={30}>March</MenuItem>
                <MenuItem value={30}>March</MenuItem>

              </Select>
            </FormControl>

          </Grid>
          <Grid item marginTop={1} lg={2}>
            <FormControl className={classes.formControl}>
                <InputLabel id="demo-simple-select-label">Upload type</InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                  style={{width:"110px"}}
                  onChange={handleMonthChange}
                >
                  {months.map((element,i) => {
                      return <MenuItem  key={i} value={element}>{element}</MenuItem>
                    })
                  }
                </Select>
            </FormControl>
          </Grid>
          <Grid item lg={12}>
            <MyDropZone setFile={setFile} setIsFileValid={setIsFileValid} setFileName={setFileName}/>
          
          </Grid>
          <Grid item lg={12}>
            {renderFileName()}
          </Grid>
        </Grid>
      </CardContent>
      <CardActions>
            <Button onClick={handleUploadClick} className='btn'>Upload</Button>
      </CardActions>
    </Card>
  );
}
