import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Dropzone from 'react-dropzone'
import { AttachFile, Description, PictureAsPdf } from '@material-ui/icons';
import { DropzoneArea } from 'material-ui-dropzone';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import ReactFileReader from 'react-file-reader';
import { Box, CardHeader, Divider, Grid, TextField } from '@material-ui/core';
import PublishIcon from '@material-ui/icons/Publish';
import CompanySelect from '../dashboard/CompanyPicker';

const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  
  title: {
    fontSize: 14,
  },
 
});

export default function UploadFileCard() {
  const classes = useStyles();

  const handleFiles = (files) => {
      var reader = new FileReader();
      reader.onload = function(e) {
          // Use reader.result
          console.log(reader)
          console.log(reader.result)
      }
      reader.readAsText(files[0]);
  }

  const handlePreviewIcon = (fileObject, classes) => {
  const {type} = fileObject.file
  const iconProps = {
    className : classes.image,
  }


  switch (type) {
    case "application/msword":
    case "application/vnd.openxmlformats-officedocument.wordprocessingml.document":
      return <Description {...iconProps} />
    case "application/pdf":
      return <PictureAsPdf {...iconProps} />
    default:
      return <AttachFile {...iconProps} />
  }
}


  return (
    <Card >
      <Box color="whitesmoke" bgcolor="info.main">
        <Grid container>
          <Grid item lg={11}>
            <Typography paddingLeft='1%' variant="h2" component="h2">
              Upload file
            </Typography>
          </Grid>
          <Grid item justifySelf="flex-end" lg={1}>
            <PublishIcon fontSize="large"/>
          </Grid>
        </Grid>
        
      </Box>
      <CardContent>
        <Grid container>
          {/* <Dropzone onDrop={acceptedFiles => console.log(acceptedFiles)}>
            {({getRootProps, getInputProps}) => (
              <section>
                <div {...getRootProps()}>
                  <input {...getInputProps()} />
                  <p>Drag 'n' drop some files here, or click to select files</p>
                </div>
              </section>
            )}
          </Dropzone> */}
          <TextField required id="standard-required" label="year" placeholder="2021" />
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={age}
            onChange={handleChange}
          >
            <MenuItem value={10}>January</MenuItem>
            <MenuItem value={20}>February</MenuItem>
            <MenuItem value={30}>March</MenuItem>
            <MenuItem value={30}>March</MenuItem>

          </Select>
          <DropzoneArea getPreviewIcon={handlePreviewIcon}/>
          <Typography className={classes.title} color="textSecondary" gutterBottom>
            No files uploaded
          </Typography>
        </Grid>
      </CardContent>
      <CardActions>
          <ReactFileReader handleFiles={handleFiles} fileTypes={'.csv'}>
            <Button className='btn'>Upload</Button>
          </ReactFileReader>
      </CardActions>
    </Card>
  );
}
